mapper.select:
select *
from (
	select rownum rn, 
	from (
		select *
		from libraryboard


				and user_id = #{userId}


				and board_num = #{num}


		order by board_date desc
	)
)
where rn between #{startPage} and #{endPage}

mapper.update:
update libraryboard
board_subject = #{boardSubject}, board_content = #{boardContent}, original_file_name = #{originalFileName}, store_file_name = #{storeFileName}, file_size = #{fileSize},
where board_num = #{boardNum}

update libraryboard
set read_count = read_count + 1
where board_num = #{boardNum}
