--  DDL for View EMPVIEW1
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "EMPVIEW1" ("EMPLOYEE_ID", "LAST_NAME", "EMAIL", "HIRE_DATE", "JOB_ID", "DEPARTMENT_ID", "DEPARTMENT_NAME") AS 
  SELECT
    e.employee_id, e.last_name, e.email, e.hire_date, e.job_id, d.department_id, d.department_name
FROM employees e JOIN departments d
ON e.department_id = d.department_id
;
--------------------------------------------------------
--  DDL for View EMPVIEW2
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "EMPVIEW2" ("EMPLOYEE_ID", "LAST_NAME", "EMAIL", "HIRE_DATE", "JOB_ID", "DEPARTMENT_ID") AS 
  SELECT
    employee_id, last_name, email, hire_date, job_id, department_id
FROM employees
WHERE department_id = 20
WITH CHECK OPTION  CONSTRAINT "EMPV20"
;
--------------------------------------------------------
--  DDL for View EMP_INFO
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "EMP_INFO" ("ID_NUMBER", "NAME", "SAL", "HIRE_DATE", "DEPT_ID", "JOB_ID") AS 
  SELECT
    employee_id, last_name || ' ' || first_name, salary, hire_date, department_id, job_id
FROM employees
;
--------------------------------------------------------
--  DDL for View SALVIEW
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SALVIEW" ("DEPT_ID", "AVG", "SUM", "MAX", "MIN") AS 
  SELECT
    department_id, AVG(salary), SUM(salary), MAX(salary), MIN(salary)
FROM employees
GROUP BY department_id
;
--------------------------------------------------------
--  DDL for View SALVIEW1
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SALVIEW1" ("DEPT_NAME", "AVG", "SUM", "MAX", "MIN") AS 
  SELECT
    d.department_name, AVG(e.salary), SUM(e.salary), MAX(e.salary), MIN(e.salary)
FROM employees e JOIN departments d
ON e.department_id = d.department_id
GROUP BY d.department_name
;
--------------------------------------------------------
--  DDL for View SALVIEW2
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SALVIEW2" ("EMP_ID", "LNAME", "EMAIL", "HIRE_DATE", "JOB_ID", "DEPT_ID") AS 
  SELECT
    employee_id, last_name, email, hire_date, job_id, department_id
FROM employees
;
--------------------------------------------------------
--  DDL for View V_VIEWUPD
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "V_VIEWUPD" ("T1C1", "T1C2", "T2C1", "T2C3") AS 
  SELECT
    t1.c1, t1.c2, t2.c1, t2.c3
FROM viewupd1 t1 JOIN viewupd2 t2
ON t1.c1 = t2.c1
;
--------------------------------------------------------
--  DDL for View YEARSAL
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "YEARSAL" ("EMPLOYEE_ID", "FIRST_NAME", "JOB_ID", "YEAR_SAL") AS 
  SELECT
    employee_id, first_name, job_id, salary + (salary * nvl(commission_pct, 0)) year_sal
FROM employees
;
